'use strict';

var cmp = require('./compile')();


module.exports = function() {

  var compile = function compile(sysdef, platform, cb) {
    cmp.compile(sysdef, platform, function(err, system) {
      if (err) { return cb(err); }
      system.topology.name = platform;
      cb(null, system);
    });
  };



  return {
    compile: compile,
  };
};
 

